#!/usr/bin/env bash

# Clean up all the log files generated by runs of Sightglass.
#
# Usage: ./clean.sh

# From https://stackoverflow.com/a/246128:
SCRIPT_DIR="$( cd -- "$( dirname -- "${BASH_SOURCE[0]:-$0}"; )" &> /dev/null && pwd 2> /dev/null; )";
PROJECT_DIR=$(dirname $SCRIPT_DIR)

# Benchmark each Wasm file.
for LOG_FILE in $(find $PROJECT_DIR -name 'std*.log'); do
    rm -v $LOG_FILE
done
